mapper.delete:
delete from d_diary where user_id=#{user_id}

	and dia_no=#{dia_no}


	and date(diary_day) = date(#{diary_day})

mapper.select:
select * from d_diary where user_id=#{user_id}

	and dia_no=#{dia_no}


	and diary_title =#{diary_title}


	and diary_content like CONCAT('%',#{diary_content},'%')

order by diary_day
