mapper.select:
select * from d_repeat where user_id=#{user_id}

	and rep_no=#{rep_no}


	and rep_type =#{rep_type}


	and rep_title like CONCAT('%',#{rep_title},'%')


	and rep_day =#{rep_day}


	and ((date(rep_start) <= date(#{rep_start}) and date(rep_end) >= date(#{rep_start}))
	or (date(rep_start) <= date(#{rep_end}) and date(rep_end) >= date(#{rep_end}))
	or (date(rep_start) >= date(#{rep_start}) and date(rep_end) <= date(#{rep_end})))

	and ((TIME_TO_SEC(rep_start) <= TIME_TO_SEC(#{rep_start}) and TIME_TO_SEC(rep_end) >= TIME_TO_SEC(#{rep_start}))
	or (TIME_TO_SEC(rep_start) <= TIME_TO_SEC(#{rep_end}) and TIME_TO_SEC(rep_end) >= TIME_TO_SEC(#{rep_end}))
	or (TIME_TO_SEC(rep_start) >= TIME_TO_SEC(#{rep_start}) and TIME_TO_SEC(rep_end) <= TIME_TO_SEC(#{rep_end})))


order by rep_start
